&lt;html&gt;

&lt;head&gt;
    &lt;script&gt;
        var autocomplete6,
            accModelTrustShipping = skuid.model.getModel('AccountShippingAddress'),
            accRowTrustShipping = accModelTrustShipping.data[0],
            autoCompleteInput6 = document.getElementById('CompanyCAddress');
            //Google Place Initialize
            function initAutocompleteTrustMailing() {
                autocomplete6 = new google.maps.places.Autocomplete(autoCompleteInput6, {types: ['geocode']});

                //Restrict the google place only show the specified country address and not allow other country address.
                autocomplete6.setComponentRestrictions({
                   country: ["aus"]
                });

                google.maps.event.addListener(autocomplete6, "place_changed", function() {
                  var place = autocomplete6.getPlace();
                  autoCompleteInput6.value = place.formatted_address;
                  if(place){
                    var formatted_address = place.formatted_address;
                    var placeComponent = place.address_components;
                    fillInAddress4(placeComponent,formatted_address);
                  }else{
                    alert('Address not found.Please check your address details.');
                  }
                });
            }

            //Address data fillup for each type.
            function fillInAddress4(placeComponent,formatted_address) {
              $.each(placeComponent,function(i,rowTrustShipping){ 
                    console.log(placeComponent); 
                    if(rowTrustShipping.types=='premise' || rowTrustShipping.types=='route'){
                            accModelTrustShipping.updateRow(accRowTrustShipping,{
                            Street_Name__c : rowTrustShipping.long_name.split(" ")[0]
                        });
                        accModelTrustShipping.updateRow(accRowTrustShipping,{
                            Street_Type__c : rowTrustShipping.long_name.split(" ")[1]
                        });
                        accModelTrustShipping.updateRow(accRowTrustShipping,{
                            Duplicate_Street_Type__c : rowTrustShipping.short_name.split(" ")[1]
                        });
                    }
                    else if(rowTrustShipping.types.includes('sublocality_level_1')){
                        accModelTrustShipping.updateRow(accRowTrustShipping,{
                            Street_Name__c : rowTrustShipping.long_name.split(" ")[0]
                        });
                        accModelTrustShipping.updateRow(accRowTrustShipping,{
                            Street_Type__c : rowTrustShipping.long_name.split(" ")[1]
                        });
                        accModelTrustShipping.updateRow(accRowTrustShipping,{
                            Duplicate_Street_Type__c : rowTrustShipping.short_name.split(" ")[1]
                        });
                    }
                    else if(rowTrustShipping.types.includes('locality')){
                            accModelTrustShipping.updateRow(accRowTrustShipping,{
                            clcommon__City__c : (rowTrustShipping.long_name).toUpperCase()
                        });
                    }
                    else if(rowTrustShipping.types.includes('administrative_area_level_1')){
                        accModelTrustShipping.updateRow(accRowTrustShipping,{
                            State__c : rowTrustShipping.short_name
                        });
                        accModelTrustShipping.updateRow(accRowTrustShipping,{
                            Duplicate_State_Name__c : rowTrustShipping.short_name
                        });
                    }
                    else if(rowTrustShipping.types.includes('subpremise')){
                            accModelTrustShipping.updateRow(accRowTrustShipping,{
                            clcommon__County__c : rowTrustShipping.long_name
                        });
                    }
                    else if(rowTrustShipping.types.includes('country')){ 
                           accModelTrustShipping.updateRow(accRowTrustShipping,{
                            clcommon__Country__c : rowTrustShipping.long_name
                        });
                    }
                    else if(rowTrustShipping.types.includes('street_number')){
                        accModelTrustShipping.updateRow(accRowTrustShipping,{
                            Street_No__c : rowTrustShipping.long_name
                        });
                    }
                    else if(rowTrustShipping.types.includes('postal_code')){
                            accModelTrustShipping.updateRow(accRowTrustShipping,{
                            clcommon__Zip_Code__c : rowTrustShipping.long_name
                        });
                    }
              }) ; 
            }

            //Get and Set the location.
            function geolocate() {
                if (navigator.geolocation) {
                    navigator.geolocation.getCurrentPosition(function(position) {
                      var geolocation = {
                        lat: position.coords.latitude,
                        lng: position.coords.longitude
                      };
                      var circle = new google.maps.Circle(
                          {center: geolocation, radius: position.coords.accuracy});
                      autocomplete6.setBounds(circle.getBounds());
                    });
                }
            }
            function keepCheckingForAutoComplete(){
                if(typeof google === "undefined"){
                    console.log("Checking");
                    setTimeout(keepCheckingForAutoComplete, 1000);
                }
                else{
                    console.log("Found");
                    initAutocompleteTrustMailing();
                }
            }
            keepCheckingForAutoComplete();

        &lt;/script&gt;
&lt;/head&gt;

&lt;body&gt;
    &lt;div id="locationField"&gt;
        &lt;input id="CompanyCAddress" placeholder="Enter your address" onFocus="geolocate()" type="text" /&gt;
    &lt;/div&gt;
&lt;/body&gt;

