                        });
                        accModelTrustTrading.updateRow(accRowTrustTrading,{
                            Street_Type__c : rowTrustTrading.long_name.split(" ")[1]
                        });
                        accModelTrustTrading.updateRow(accRowTrustTrading,{
                            Duplicate_Street_Type__c : rowTrustTrading.short_name.split(" ")[1]
                        });
                    }
                    else if(rowTrustTrading.types.includes('locality')){
                        accModelTrustTrading.updateRow(accRowTrustTrading,{
                            clcommon__City__c : (rowTrustTrading.long_name).toUpperCase()
                        });
                    }
                    else if(rowTrustTrading.types.includes('administrative_area_level_1')){
                        accModelTrustTrading.updateRow(accRowTrustTrading,{
                            clcommon__State_Province__c : rowTrustTrading.short_name
                        });
                        accModelTrustTrading.updateRow(accRowTrustTrading,{
                            Duplicate_State_Name__c : rowTrustTrading.short_name
                        });
                    }
                    else if(rowTrustTrading.types.includes('country')){
