n></columns></fields></section><section title="New Section" collapsible="no" uniqueid="sk-1wcE-3929" showheader="false"><fields><columns uniqueid="sk-1wcG-3949" layoutmode="fixed"><column width="80%" uniqueid="sk-1wcG-3937"><sections><section title="New Section" collapsible="no" uniqueid="sk-1wcG-3938" showheader="false"><fields><field uniqueid="sk-1wcR-3998" id="Name_for_Purpose_of_GSA_Contract__c"><renderconditions logictype="and" onhidedatabehavior="cancel"><rendercondition type="fieldvalue" operator="=" enclosevalueinquotes="true" fieldmodel="Party" sourcetype="fieldvalue" field="Is_General_Security_Aggrement_Being_Take__c" fieldtargetobjects="clcommon__Party__c" value="Yes"/></renderconditions><enableconditions/></field></fields></section></sections></column><column width="20%" uniqueid="sk-1wcG-3954"><sections><section title="New Section" collapsible="no" uniqueid="sk-1wcG-3955" showheader="false"><fields><field uniqueid="sk-1wcU-4006" type="COMBO" editmodebehavior="autopopup"><label>Required</label><template><contents/></template><renderconditions logictype="and" onhidedatabehavior="keep"><rendercondition type="fieldvalue" operator="=" fieldmodel="Party" sourcetype="fieldvalue" field="Is_General_Security_Aggrement_Being_Take__c" fieldtargetobjects="clcommon__Party__c" value="Yes" enclosevalueinquotes="true"/><rendercondition type="blank" operator="=" fieldmodel="Party" sourcetype="fieldvalue" field="Name_for_Purpose_of_GSA_Contract__c" fieldtargetobjects="clcommon__Party__c" value="null" enclosevalueinquotes="false"/></renderconditions><enableconditions/></field></fields></section></sections><renderconditions logictype="and"/></column></columns></fields></section></sections><renderconditions logictype="and"/></column></columns><renderconditions logictype="and"/></basicfieldeditor><basicfieldeditor showheader="true" showsavecancel="false" showerrorsinline="true" model="Account" uniqueid="sk-2AwF-731" mode="edit"><columns layoutmode="fixed"><column width="100%" uniqueid="sk-2AwF-732"><sections><section collapsible="no" uniqueid="sk-3TkJ-5846" showheader="false"><fields><columns uniqueid="sk-21vo-3271"><column width="80%" uniqueid="sk-21vo-3258"><sections><section title="Registered Address" collapsible="no" uniqueid="sk-21vr-3281"><fields><field uniqueid="sk-3Tl7-6234" type="COMBO" editmodebehavior="autopopup" allowhtml="true"><label>Address</label><template model="ABN_ComapnyName_Model">&lt;!DOCTYPE html&gt;
&lt;html&gt;

&lt;head&gt;
    &lt;script&gt;
        var autocomplete3,
            accModelRegComp = skuid.model.getModel('AccountBillingAddress'),
            accRowRegComp = accModelRegComp.data[0],
            autoCompleteInput3 = document.getElementById('CompanyRegisteredAddressId');
            //Google Place Initialize
            function initAutocompleteCompanyRegisterAddress() {
                autocomplete3 = new google.maps.places.Autocomplete(autoCompleteInput3, {types: ['geocode']});

                //Restrict the google place only show the specified country address and not allow other country address.
                autocomplete3.setComponentRestrictions({
                   country: ["aus"]
                });

                google.maps.event.addListener(autocomplete3, "place_changed", function() {
                  var place = autocomplete3.getPlace();
                  autoCompleteInput3.value = place.formatted_address;
                  if(place){
                    var formatted_address = place.formatted_address;
                    var placeComponent = place.address_components;
                    fillInAddressCompanyRegAddress(placeComponent,formatted_address);
                  }else{
                    alert('Address not found.Please check your address details.');
                  }
                });
            }

            //Address data fillup for each type.
            function fillInAddressCompanyRegAddress(placeComponent,formatted_address) {
              $.each(placeComponent,function(i,rowComp){ 
                    if(rowComp.types=='premise' || rowComp.types=='route'){
                         accModelRegComp.updateRow(accRowRegComp,{
                            Street_Name__c : rowComp.long_name.split(" ")[0]
                        });
                        accModelRegComp.updateRow(accRowRegComp,{
                            Street_Type__c : rowComp.long_name.split(" ")[1]
                        });
                        accModelRegComp.updateRow(accRowRegComp,{
                            Duplicate_Street_Type__c : rowComp.short_name.split(" ")[1]
                        });
                    }
                    else if(rowComp.types.includes('sublocality_level_1')){
                        accModelRegComp.updateRow(accRowRegComp,{
                            Street_Name__c : rowComp.long_name.split(" ")[0]
                        });
                        accModelRegComp.updateRow(accRowRegComp,{
                            Street_Type__c : rowComp.long_name.split(" ")[1]
                        });
                        accModelRegComp.updateRow(accRowRegComp,{
                            Duplicate_Street_Type__c : rowComp.short_name.split(" ")[1]
                        });
                    }
                    else if(rowComp.types.includes('locality')){
                        accModelRegComp.updateRow(accRowRegComp,{
                            clcommon__City__c : (rowComp.long_name).toUpperCase()
                        });
                    }
                    else if(rowComp.types.includes('administrative_area_level_1')){
                        accModelRegComp.updateRow(accRowRegComp,{
                            State__c : rowComp.short_name
                        });
                        accModelRegComp.updateRow(accRowRegComp,{
                            Duplicate_State_Name__c : rowComp.short_name
                        });
                    }
                    else if(rowComp.types.includes('subpremise')){
                        accModelRegComp.updateRow(accRowRegComp,{
                            clcommon__County__c : rowComp.long_name
                        });
                    }
                    else if(rowComp.types.includes('country')){
                        accModelRegComp.updateRow(accRowRegComp,{
                            clcommon__Country__c : rowComp.long_name
                        });
                    }
                    else if(rowComp.types.includes('street_number')){
                        accModelRegComp.updateRow(accRowRegComp,{
                            Street_No__c : rowComp.long_name
                        });
                    }
                    else if(rowComp.types.includes('postal_code')){
                        accModelRegComp.updateRow(accRowRegComp,{
                            clcommon__Zip_Code__c : rowComp.long_name
                        });
                    }
              }) ; 
            }

            //Get and Set the location.
            function geolocate() {
                if (navigator.geolocation) {
                    navigator.geolocation.getCurrentPosition(function(position) {
                      var geolocation = {
                        lat: position.coords.latitude,
                        lng: position.coords.longitude
                      };
                      var circle = new google.maps.Circle(
                          {center: geolocation, radius: position.coords.accuracy});
                      autocomplete3.setBounds(circle.getBounds());
                    });
                }
            }
            function keepCheckingForAutoComplete(){
                if(typeof google === "undefined"){
                    setTimeout(keepCheckingForAutoComplete, 1000);
                }
                else{
                    initAutocompleteCompanyRegisterAddress();
                }
            }
            keepCheckingForAutoComplete();


        &lt;/script&gt;
&lt;/head&gt;

&lt;body&gt;
    &lt;div id="locationField"&gt;
        &lt;input id="CompanyRegisteredAddressId" placeholder="Enter your address" onFocus="geolocate()" type="text" /&gt;
    &lt;/div&gt;
