uniqueid="sk-2Eda-9274" showheader="false"><fields/></section><section collapsible="no" uniqueid="sk-2Eda-9275" title="Trading Address"><fields><field uniqueid="sk-2Eda-9276" type="COMBO" editmodebehavior="autopopup" allowhtml="true"><label>Address</label><template>&lt;!DOCTYPE html&gt;
&lt;html&gt;

&lt;head&gt;
    &lt;script&gt;
        var autocomplete7,
            accModelTrustTrading = skuid.model.getModel('AccountTradingAddress'),
            accRowTrustTrading = accModelTrustTrading.data[0],
            autoCompleteInput7 = document.getElementById('CompanyTAddress');
            //Google Place Initialize
            function initAutocompleteTrustTrading() {
                autocomplete7 = new google.maps.places.Autocomplete(autoCompleteInput7, {types: ['geocode']});

                //Restrict the google place only show the specified country address and not allow other country address.
                autocomplete7.setComponentRestrictions({
                   country: ["aus"]
                });

                google.maps.event.addListener(autocomplete7, "place_changed", function() {
                  var place = autocomplete7.getPlace();
                  autoCompleteInput7.value = place.formatted_address;
                  if(place){
                    var formatted_address = place.formatted_address;
                    var placeComponent = place.address_components;
                    fillInAddress3(placeComponent,formatted_address);
                  }else{
                    alert('Address not found.Please check your address details.');
                  }
                });
            }

            //Address data fillup for each type.
            function fillInAddress3(placeComponent,formatted_address) {
              $.each(placeComponent,function(i,rowTrustTrading){
                    if(rowTrustTrading.types=='premise' || rowTrustTrading.types=='route'){
                            accModelTrustTrading.updateRow(accRowTrustTrading,{
                            Street_Name__c : rowTrustTrading.long_name.split(" ")[0]
                        });
                        accModelTrustTrading.updateRow(accRowTrustTrading,{
                            Street_Type__c : rowTrustTrading.long_name.split(" ")[1]
                        });
                        accModelTrustTrading.updateRow(accRowTrustTrading,{
                            Duplicate_Street_Type__c : rowTrustTrading.short_name.split(" ")[1]
                        });
                    }
                    else if(rowTrustTrading.types.includes('sublocality_level_1')){
                        accModelTrustTrading.updateRow(accRowTrustTrading,{
                            Street_Name__c : rowTrustTrading.long_name.split(" ")[0]
                        });
                        accModelTrustTrading.updateRow(accRowTrustTrading,{
                            Street_Type__c : rowTrustTrading.long_name.split(" ")[1]
                        });
                        accModelTrustTrading.updateRow(accRowTrustTrading,{
                            Duplicate_Street_Type__c : rowTrustTrading.short_name.split(" ")[1]
                        });
                    }
                    else if(rowTrustTrading.types.includes('locality')){
                        accModelTrustTrading.updateRow(accRowTrustTrading,{
                            clcommon__City__c : (rowTrustTrading.long_name).toUpperCase()
                        });
                    }
                    else if(rowTrustTrading.types.includes('administrative_area_level_1')){
                        accModelTrustTrading.updateRow(accRowTrustTrading,{
                            clcommon__State_Province__c : rowTrustTrading.short_name
                        });
                        accModelTrustTrading.updateRow(accRowTrustTrading,{
                            Duplicate_State_Name__c : rowTrustTrading.short_name
                        });
                    }
                    else if(rowTrustTrading.types.includes('country')){
                        accModelTrustTrading.updateRow(accRowTrustTrading,{
                            clcommon__Country__c : rowTrustTrading.long_name
                        });
                    }
                    else if(rowTrustTrading.types.includes('street_number')){
                        accModelTrustTrading.updateRow(accRowTrustTrading,{
                            Street_No__c : rowTrustTrading.long_name
                        });
                    }
                    else if(rowTrustTrading.types.includes('postal_code')){
                        accModelTrustTrading.updateRow(accRowTrustTrading,{
                            clcommon__Zip_Code__c : rowTrustTrading.long_name
                        });
                    }
              }) ; 
            }

            //Get and Set the location.
            function geolocate() {
                if (navigator.geolocation) {
                    navigator.geolocation.getCurrentPosition(function(position) {
                      var geolocation = {
                        lat: position.coords.latitude,
                        lng: position.coords.longitude
                      };
                      var circle = new google.maps.Circle(
                          {center: geolocation, radius: position.coords.accuracy});
                      autocomplete7.setBounds(circle.getBounds());
                    });
                }
            }
            function keepCheckingForAutoComplete(){
                if(typeof google === "undefined"){
                    setTimeout(keepCheckingForAutoComplete, 1000);
                }
                else{
                    initAutocompleteTrustTrading();
                }
            }
            keepCheckingForAutoComplete();



        &lt;/script&gt;
&lt;/head&gt;

&lt;body&gt;
    &lt;div id="locationField"&gt;
        &lt;input id="CompanyTAddress" placeholder="Enter your address" onFocus="geolocate()" type="text" /&gt;
    &lt;/div&gt;
&lt;/body&gt;

