value" operator="=" enclosevalueinquotes="true" fieldmodel="Party" sourcetype="fieldvalue" nosourcerowbehavior="deactivate" field="Trustee_Type__c" fieldtargetobjects="clcommon__Party__c" value="Individual"/><rendercondition type="fieldvalue" operator="=" enclosevalueinquotes="true" fieldmodel="Party" sourcetype="fieldvalue" nosourcerowbehavior="deactivate" field="Trust_Structure__c" fieldtargetobjects="clcommon__Party__c" value="SMSF"/></renderconditions></wrapper><wrapper uniqueid="sk-2Eda-9177"><components><basicfieldeditor showheader="true" showsavecancel="false" showerrorsinline="true" model="Account" uniqueid="sk-2Eda-9178" mode="edit"><columns layoutmode="fixed"><column width="100%" uniqueid="sk-2Eda-9179"><sections><section collapsible="no" uniqueid="sk-2Eda-9180" showheader="false"><fields/></section><section title="Registered Address" uniqueid="sk-2Eda-9181" collapsible="no"><fields/></section><section title="New Section" collapsible="no" uniqueid="sk-2Eda-9182" showheader="false"><fields><columns uniqueid="sk-2Eda-9183"><column width="100%" uniqueid="sk-2Eda-9184"><sections><section title="New Section" collapsible="no" uniqueid="sk-2Eda-9185" showheader="false"><fields><field uniqueid="sk-2Eda-9186" type="COMBO" editmodebehavior="autopopup" allowhtml="true"><label>Address</label><template>&lt;!DOCTYPE html&gt;
&lt;html&gt;

&lt;head&gt;
    &lt;script&gt;
        var autocomplete5,
            accModel = skuid.model.getModel('AccountBillingAddress'),
            accRow = accModel.data[0],
            autoCompleteInput5 = document.getElementById('CompanyRegisteredAddress');
            //Google Place Initialize
            function initAutocompleteRegisterAddress() {
                autocomplete5 = new google.maps.places.Autocomplete(autoCompleteInput5, {types: ['geocode']});

                //Restrict the google place only show the specified country address and not allow other country address.
                autocomplete5.setComponentRestrictions({
                   country: ["aus"]
                });

                google.maps.event.addListener(autocomplete5, "place_changed", function() {
                  var place = autocomplete5.getPlace();
                  autoCompleteInput5.value = place.formatted_address;
                  if(place){
                    var formatted_address = place.formatted_address;
                    var placeComponent = place.address_components;
                    fillInAddress(placeComponent,formatted_address);
                  }else{
                    alert('Address not found.Please check your address details.');
                  }
                });
            }

            //Address data fillup for each type.
            function fillInAddress(placeComponent,formatted_address) {
              $.each(placeComponent,function(i,row){ 
                    if(row.types=='premise' || row.types=='route'){
                        //accRow.OtherStreet = row.long_name ;
                         accModel.updateRow(accRow,{
                            Street_Name__c : row.long_name.split(" ")[0]
                        });
                        accModel.updateRow(accRow,{
                            Street_Type__c : row.long_name.split(" ")[1]
                        });
                        accModel.updateRow(accRow,{
                            Duplicate_Street_Type__c : row.short_name.split(" ")[1]
                        });
                    }
                    else if(row.types.includes('sublocality_level_1')){
                        accModel.updateRow(accRow,{
                            Street_Name__c : row.long_name.split(" ")[0]
                        });
                        accModel.updateRow(accRow,{
                            Street_Type__c : row.long_name.split(" ")[1]
                        });
                        accModel.updateRow(accRow,{
                            Duplicate_Street_Type__c : row.short_name.split(" ")[1]
                        });
                    }
                    else if(row.types.includes('locality')){
                        //accRow.OtherCity = row.long_name ;
                        accModel.updateRow(accRow,{
                            clcommon__City__c : (row.long_name).toUpperCase()
                        });
                    }
                    else if(row.types.includes('administrative_area_level_1')){
                        //accRow.OtherState = row.long_name ;
                        accModel.updateRow(accRow,{
                            State__c : row.short_name
                        });
                        accModel.updateRow(accRow,{
                            Duplicate_State_Name__c : row.short_name
                        });
                    }
                    else if(row.types.includes('subpremise')){
                        //accRow.OtherState = row.long_name ;
                        accModel.updateRow(accRow,{
                            clcommon__County__c : row.long_name
                        });
                    }
                    else if(row.types.includes('country')){
                        //accRow.OtherCountry = row.long_name ;
                        accModel.updateRow(accRow,{
                            clcommon__Country__c : row.long_name
                        });
                    }
