var params = arguments[0],
	$ = skuid.$;
var partyModelIdenCheck = skuid.model.getModel('CIParty');
var attModelIdenModel = skuid.model.getModel('PartyAttachments');
var attIds = [];
var partyModelIdenRowCheck = partyModelIdenCheck.data[0];
var records = sforce.connection.query("Select ID from clcommon__Document_Category__c where clcommon__Category_Name__c = 'Identity Documents' and genesis__Application__c = '"+partyModelIdenRowCheck.genesis__Application__c+"' and clcommon__Parent_Document_Category__c != null and clcommon__Account__c = '"+partyModelIdenRowCheck.clcommon__Account__c+"' limit 1");
var documentCategoryRecord = records.getArray('records');
var resCheck;
$.each(attModelIdenModel.data,function(i,row){
    attIds.push(row.Id+"");
});
if(documentCategoryRecord[0].Id !== undefined && attModelIdenModel.data.length > 0){
    resCheck = sforce.apex.execute('CategoryAttachmentUtility','categoryAttachmentCheckAndCreation',
        {
            documentCategoryId : documentCategoryRecord[0].Id, 
            attachIdList : attIds
        });
}
else{
    alert('Document Category Not Found or Uploaded Documents could not be recognised.Please contact Admin as category Attachments could not be created');
}

