    {
        attachId : records.row.Id
    });
alert(resCheck);

var params = arguments[0],
	$ = skuid.$;
var partyModel = skuid.model.getModel('CIParty');
var partyRow = partyModel.data[0];
var contactObj = new sforce.SObject("Contact");
contactObj.Id = partyRow.clcommon__Contact__c;
if(partyRow.Politically_Exposed_Person__c !== undefined && partyRow.Politically_Exposed_Person__c !== null){
    if(partyRow.Politically_Exposed_Person__c == 'Yes'){
        contactObj.PEP__c = true;
    }
    else if(partyRow.Politically_Exposed_Person__c == 'No'){
        contactObj.PEP__c = false;
    }
    else{
        contactObj.PEP__c = false;
    }
}
var result = sforce.connection.update([contactObj]);

