var incomeRowsToUpdate = {};
$.each(incomeModel.getRows(),function(i,row){
    if(row.Company_Type_of_Documents_Sighted__c === undefined){
        incomeRowsToUpdate[row.Id] = { Type_of_Documents_Sighted_Warning_Check__c: 'Warning1' };
    } else {
        var docCount = (row.Company_Type_of_Documents_Sighted__c.split(";")).length;
        if(Number(docCount) < 3 && ((row.Employment_Information__c !== null && row.Employment_Information__c !== undefined && row.Employment_Information__r.Party__c !== undefined && row.Employment_Information__r.Party__c !== null && row.Employment_Information__r.Party__r.Risk_Grade__c === 'Prime') || (row.Party__c !== null && row.Party__c !== undefined && row.Party__r.Risk_Grade__c === 'Prime'))){
            incomeRowsToUpdate[row.Id] = { Type_of_Documents_Sighted_Warning_Check__c: 'Warning2' };
        } else if(Number(docCount) < 2){
            incomeRowsToUpdate[row.Id] = { Type_of_Documents_Sighted_Warning_Check__c: 'Warning1' };
        } else {
            incomeRowsToUpdate[row.Id] = { Type_of_Documents_Sighted_Warning_Check__c: undefined };
        }
    }
});
incomeModel.updateRows( incomeRowsToUpdate );

