var records = arguments[0].item;
if(records.row.Exception_Recommender__c === undefined || records.row.Exception_Recommender__c === ''){
    alert('Exception must be recommended before it can be Approved/Declined');
    return false;
}
if(records.row.Exception_Recommender__c === skuid.utils.userInfo.name){
    alert('The same user cannot Recommend and Approve/Decline the same exception');
    return false;
