var params = arguments[0],
	$ = skuid.$;
var appModelIdenCheck = skuid.model.getModel('CRApplication');
var attModelIdenModel = skuid.model.getModel('ConditionDocumentsAttachments');
var attIds = [];
var appModelIdenRowCheck = appModelIdenCheck.data[0];
var records = sforce.connection.query("Select ID from clcommon__Document_Category__c where clcommon__Category_Name__c = 'Condition Documents' and genesis__Application__c = '"+appModelIdenRowCheck.Id+"' and clcommon__Parent_Document_Category__r.clcommon__Category_Name__c = 'Review Documents' limit 1");
var documentCategoryRecord = records.getArray('records');
var resCheck;
$.each(attModelIdenModel.data,function(i,row){
    attIds.push(row.Id+"");
});
if(documentCategoryRecord[0].Id !== undefined && attModelIdenModel.data.length > 0){
    resCheck = sforce.apex.execute('CategoryAttachmentUtility','categoryAttachmentCheckAndCreation',
        {
            documentCategoryId : documentCategoryRecord[0].Id, 
            attachIdList : attIds
        });
}
else{
    alert('Document Category Not Found or Uploaded Documents could not be recognised.Please contact Admin as category Attachments could not be created');
}

